import { Duration } from 'lodash/moment/src/lib/duration/constructor';

var proto = Duration.prototype;

import { abs } from 'lodash/moment/src/lib/duration/abs';
import { add, subtract } from 'lodash/moment/src/lib/duration/add-subtract';
import {
    as,
    asMilliseconds,
    asSeconds,
    asMinutes,
    asHours,
    asDays,
    asWeeks,
    asMonths,
    asQuarters,
    asYears,
    valueOf,
} from 'lodash/moment/src/lib/duration/as';
import { bubble } from 'lodash/moment/src/lib/duration/bubble';
import { clone } from 'lodash/moment/src/lib/duration/clone';
import {
    get,
    milliseconds,
    seconds,
    minutes,
    hours,
    days,
    months,
    years,
    weeks,
} from 'lodash/moment/src/lib/duration/get';
import { humanize } from 'lodash/moment/src/lib/duration/humanize';
import { toISOString } from 'lodash/moment/src/lib/duration/iso-string';
import { lang, locale, localeData } from 'lodash/moment/src/lib/moment/locale';
import { isValid } from 'lodash/moment/src/lib/duration/valid';

proto.isValid = isValid;
proto.abs = abs;
proto.add = add;
proto.subtract = subtract;
proto.as = as;
proto.asMilliseconds = asMilliseconds;
proto.asSeconds = asSeconds;
proto.asMinutes = asMinutes;
proto.asHours = asHours;
proto.asDays = asDays;
proto.asWeeks = asWeeks;
proto.asMonths = asMonths;
proto.asQuarters = asQuarters;
proto.asYears = asYears;
proto.valueOf = valueOf;
proto._bubble = bubble;
proto.clone = clone;
proto.get = get;
proto.milliseconds = milliseconds;
proto.seconds = seconds;
proto.minutes = minutes;
proto.hours = hours;
proto.days = days;
proto.weeks = weeks;
proto.months = months;
proto.years = years;
proto.humanize = humanize;
proto.toISOString = toISOString;
proto.toString = toISOString;
proto.toJSON = toISOString;
proto.locale = locale;
proto.localeData = localeData;

// Deprecations
import { deprecate } from 'lodash/moment/src/lib/utils/deprecate';

proto.toIsoString = deprecate(
    'toIsoString() is deprecated. Please use toISOString() instead (notice the capitals)',
    toISOString
);
proto.lang = lang;
