import { createLocal } from 'lodash/moment/src/lib/create/local';
import { createUTC } from 'lodash/moment/src/lib/create/utc';
import { createInvalid } from 'lodash/moment/src/lib/create/valid';
import { isMoment } from 'lodash/moment/src/lib/moment/constructor';
import { min, max } from 'lodash/moment/src/lib/moment/min-max';
import { now } from 'lodash/moment/src/lib/moment/now';
import momentPrototype from 'lodash/moment/src/lib/moment/prototype';

function createUnix(input) {
    return createLocal(input * 1000);
}

function createInZone() {
    return createLocal.apply(null, arguments).parseZone();
}

export {
    now,
    min,
    max,
    isMoment,
    createUTC,
    createUnix,
    createLocal,
    createInZone,
    createInvalid,
    momentPrototype,
};
