import { isValid as _isValid } from 'lodash/moment/src/lib/create/valid';
import extend from 'lodash/moment/src/lib/utils/extend';
import getParsingFlags from 'lodash/moment/src/lib/create/parsing-flags';

export function isValid() {
    return _isValid(this);
}

export function parsingFlags() {
    return extend({}, getParsingFlags(this));
}

export function invalidAt() {
    return getParsingFlags(this).overflow;
}
